mapper.select:
SELECT A.BOARD_CONTENT_NO AS boardContentNo, A.ORG_BOARD_CONTENT_NO AS orgBoardContentNo, A.TITLE AS title, A.CONTENT AS content, A.SEC_YN AS secYn, A.USER_NO AS userNo, B.USER_NAME AS userName, A.VIEW_CNT AS viewCnt, A.REG_DATE AS regDdate, IF((SELECT count(BOARD_CONTENT_NO) FROM bizmiso.AI_BOARD_CONTENT ABC WHERE ABC.ORG_BOARD_CONTENT_NO = A.BOARD_CONTENT_NO) = 0,'N','Y') AS replyYn FROM bizmiso.AI_BOARD_CONTENT A INNER JOIN bizmiso.AI_USER B ON A.USER_NO = B.USER_NO INNER JOIN bizmiso.AI_BOARD C ON A.BOARD_NO = C.BOARD_NO WHERE 1=1
AND A.BOARD_NO = #{boardNo}
AND C.SPORTS_BOARD_CODE = #{sportsBoardCode}
ORDER BY A.CONTENT_GROUP DESC, A.REG_DATE ASC LIMIT #{pagePerRow} offset #{startRow}

SELECT COUNT(*) FROM bizmiso.AI_BOARD_CONTENT A INNER JOIN bizmiso.AI_USER B ON A.USER_NO = B.USER_NO INNER JOIN bizmiso.AI_BOARD C ON A.BOARD_NO = C.BOARD_NO WHERE 1=1
AND A.BOARD_NO = #{boardNo}
AND C.SPORTS_BOARD_CODE = #{sportsBoardCode}

mapper.insert:
select MAX(BOARD_CONTENT_NO) + 1 as SEQUENCE_NUMBER FROM bizmiso.AI_BOARD_CONTENT
INSERT INTO bizmiso.AI_BOARD_CONTENT ( BOARD_CONTENT_NO, USER_NO, BOARD_NO, ORG_BOARD_CONTENT_NO, TITLE, CONTENT, SEC_YN, VIEW_CNT, REG_DATE, UPD_DATE, REPLY_YN, CONTENT_GROUP ) VALUES ( #{boardContentNo}, #{userNo}, #{boardNo}, #{orgBoardContentNo}, #{title}, #{content}, #{secYn}, 0, now(), now(), 'N',
#{orgBoardContentNo}
#{boardContentNo}
)
