mapper.select:
SELECT A.BOARD_CONTENT_NO AS boardContentNo, A.ORG_BOARD_CONTENT_NO AS orgBoardContentNo, A.TITLE AS title, A.CONTENT AS content, A.SEC_YN AS secYn, A.USER_NO AS userNo, A.VIEW_CNT AS viewCnt, A.REG_DATE AS regDate, A.BOARD_NO AS boardNo, B.USER_NAME AS userName, C.BOARD_TYPE_CODE AS boardTypeCode, (SELECT count(BOARD_CONTENT_NO) FROM AI_BOARD_CONTENT ABC WHERE ABC.ORG_BOARD_CONTENT_NO = A.BOARD_CONTENT_NO) AS replyCnt FROM AI_BOARD_CONTENT A INNER JOIN AI_USER B ON A.USER_NO = B.USER_NO INNER JOIN AI_BOARD C ON A.BOARD_NO = C.BOARD_NO WHERE 1=1
AND A.BOARD_NO = #{boardNo}
AND C.BOARD_TYPE_CODE = 'CD006001'
AND C.BOARD_TYPE_CODE = 'CD006002'
ORDER BY A.CONTENT_GROUP DESC, A.REG_DATE ASC LIMIT #{pagePerRow} offset #{startRow}

mapper.insert:
SELECT ai_get_seq('boardContentNo') FROM DUAL
INSERT INTO AI_BOARD_CONTENT ( BOARD_CONTENT_NO, USER_NO, BOARD_NO, ORG_BOARD_CONTENT_NO, TITLE, CONTENT, SEC_YN, VIEW_CNT, REG_DATE, UPD_DATE, CONTENT_GROUP ) VALUES ( #{boardContentNo}, #{userNo}, #{boardNo}, #{orgBoardContentNo}, #{title}, #{content}, #{secYn}, 0, now(), now(),
#{orgBoardContentNo}
#{boardContentNo}
)
