mapper.select:
SELECT CODE as code, CODE_TYPE as codeType, PRT_CODE as prtCode, CODE_NAME as codeName, CODE_DESC as codeDesc FROM AI_COMMONCODE WHERE PRT_CODE =#{prtCode} AND DISP_YN = 'Y'
AND CODE_TYPE = #{codeType}
ORDER BY ORDER_NO ASC ;

SELECT * FROM ( SELECT FIELD(AB.BOARD_TYPE_CODE,'CD020','CD021','CD022','CD006001','CD006002') AS typeNo, AB.BOARD_TYPE_CODE AS typeCode, (SELECT CODE_NAME FROM AI_COMMONCODE WHERE CODE = AB.BOARD_TYPE_CODE) AS typeName, BOARD_CONTENT_NO AS contentNo, TITLE AS title, CONTENT AS content, DATE_FORMAT(ABC.REG_DATE, '%Y%m%d%H%i%S') AS regDate FROM AI_BOARD_CONTENT ABC INNER JOIN AI_BOARD AB ON ABC.BOARD_NO = AB.BOARD_NO UNION ALL SELECT FIELD(SPORTS_TYPE_CODE,'CD020','CD021','CD022','CD006001','CD006002') AS typeNo, SPORTS_TYPE_CODE AS typeCode, (SELECT CODE_NAME FROM AI_COMMONCODE WHERE CODE = ASD.SPORTS_TYPE_CODE) AS typeName, DATA_NO AS contentNo, FILE_NAME AS title, '' AS content, DATE_FORMAT(REG_DATE, '%Y%m%d%H%i%S') AS regDate FROM AI_SPORTS_DATA ASD ) a WHERE (a.title like CONCAT('%', #{query}, '%') OR a.content like CONCAT('%', #{query}, '%'))
AND a.typeCode = #{type}
ORDER BY typeNo desc, regDate desc LIMIT #{pagePerRow} offset #{startRow}
